  KEY `idx_libelle_compte` (`libelle_compte`),
  KEY `idx_type_entite` (`type_entite`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

