-- Vue pour afficher la hiérarchie complète SYCEBNL OFFICIEL
CREATE OR REPLACE VIEW `vue_hierarchie_complete_sycebnl_officiel` AS
SELECT 
    p.id,
    p.numero_compte,
    p.code_compte,
    p.libelle_compte,
    p.niveau,
    p.parent_id,
    p.classe,
    p.type_entite,
    parent.libelle_compte AS parent_libelle,
    CASE p.niveau
        WHEN 0 THEN 'Classe'
        WHEN 1 THEN 'Sous-classe'
        WHEN 2 THEN 'Compte'
        WHEN 3 THEN 'Sous-compte'
        WHEN 4 THEN 'Détail'
        ELSE 'Autre'
    END AS type_compte,
    CASE p.classe
        WHEN '1' THEN 'Ressources durables'
        WHEN '2' THEN 'Actif immobilisé'
        WHEN '3' THEN 'Stocks'
        WHEN '4' THEN 'Tiers'
        WHEN '5' THEN 'Trésorerie'
        WHEN '6' THEN 'Charges ordinaires'
        WHEN '7' THEN 'Produits ordinaires'
        WHEN '8' THEN 'Autres charges et produits'
        WHEN '9' THEN 'Contributions volontaires en nature'
        ELSE 'Autre'
    END AS description_classe
FROM plan_comptable p
LEFT JOIN plan_comptable parent ON p.parent_id = parent.numero_compte
ORDER BY p.numero_compte;

-- Vue pour les classes principales SYCEBNL OFFICIEL
CREATE OR REPLACE VIEW `vue_classes_sycebnl_officiel` AS
SELECT 
    *,
    CASE classe
        WHEN '1' THEN 'Ressources durables (dotations, réserves, emprunts)'
        WHEN '2' THEN 'Actif immobilisé (immobilisations, amortissements)'
        WHEN '3' THEN 'Stocks (biens liés à l''activité, dons en nature)'
        WHEN '4' THEN 'Tiers (adhérents, fournisseurs, personnel)'
        WHEN '5' THEN 'Trésorerie (banques, caisses, titres)'
        WHEN '6' THEN 'Charges des activités ordinaires'
        WHEN '7' THEN 'Produits des activités ordinaires'
        WHEN '8' THEN 'Autres charges et produits (H.A.O.)'
        WHEN '9' THEN 'Contributions volontaires en nature (spécifique SYCEBNL)'
        ELSE 'Autre'
    END AS description_detaillee
FROM plan_comptable 
WHERE niveau = 0 
ORDER BY numero_compte;

-- Vue pour les comptes spécifiques aux entités à but non lucratif
CREATE OR REPLACE VIEW `vue_comptes_ebnl_specifiques_officiel` AS
SELECT *
FROM plan_comptable
WHERE libelle_compte LIKE '%dotation%'
   OR libelle_compte LIKE '%adhérent%'
   OR libelle_compte LIKE '%fonds%'
   OR libelle_compte LIKE '%subvention%'
   OR libelle_compte LIKE '%don%'
   OR libelle_compte LIKE '%legs%'
   OR libelle_compte LIKE '%contribution%'
   OR libelle_compte LIKE '%bénévol%'
   OR classe = '9'
ORDER BY numero_compte;

-- Vue pour les comptes par classe avec statistiques
CREATE OR REPLACE VIEW `vue_comptes_par_classe_sycebnl_officiel` AS
SELECT 
    classe,
    CASE classe
        WHEN '1' THEN 'Ressources durables'
        WHEN '2' THEN 'Actif immobilisé'
        WHEN '3' THEN 'Stocks'
        WHEN '4' THEN 'Tiers'
        WHEN '5' THEN 'Trésorerie'
        WHEN '6' THEN 'Charges ordinaires'
        WHEN '7' THEN 'Produits ordinaires'
        WHEN '8' THEN 'Autres charges et produits'
        WHEN '9' THEN 'Contributions volontaires en nature'
        ELSE 'Autre'
    END AS description_classe,
    COUNT(*) as total_comptes,
    SUM(CASE WHEN niveau = 0 THEN 1 ELSE 0 END) as classes,
    SUM(CASE WHEN niveau = 1 THEN 1 ELSE 0 END) as sous_classes,
    SUM(CASE WHEN niveau = 2 THEN 1 ELSE 0 END) as comptes,
    SUM(CASE WHEN niveau = 3 THEN 1 ELSE 0 END) as sous_comptes,
    SUM(CASE WHEN niveau = 4 THEN 1 ELSE 0 END) as details
FROM plan_comptable 
GROUP BY classe 
ORDER BY classe;

-- Fonction pour obtenir le chemin hiérarchique complet SYCEBNL OFFICIEL
DELIMITER //
CREATE FUNCTION GetCheminHierarchiqueSYCEBNLOfficiel(compte_numero VARCHAR(10))
RETURNS TEXT
READS SQL DATA
DETERMINISTIC
BEGIN
    DECLARE chemin TEXT DEFAULT '';
    DECLARE current_numero VARCHAR(10) DEFAULT compte_numero;
    DECLARE current_libelle VARCHAR(255);
    DECLARE current_parent VARCHAR(10);
    DECLARE niveau_count INT DEFAULT 0;
    
    WHILE current_numero IS NOT NULL AND niveau_count < 10 DO
        SELECT libelle_compte, parent_id 
        INTO current_libelle, current_parent
        FROM plan_comptable 
        WHERE numero_compte = current_numero;
        
        IF current_libelle IS NOT NULL THEN
            IF chemin = '' THEN
                SET chemin = current_libelle;
            ELSE
                SET chemin = CONCAT(current_libelle, ' > ', chemin);
            END IF;
        END IF;
        
        SET current_numero = current_parent;
        SET niveau_count = niveau_count + 1;
    END WHILE;
    
    RETURN chemin;
END //
DELIMITER ;

-- Procédure pour afficher l'arbre hiérarchique d'une classe SYCEBNL OFFICIEL
DELIMITER //
CREATE PROCEDURE AfficherArbreClasseSYCEBNLOfficiel(IN classe_numero VARCHAR(1))
BEGIN
    SELECT 
        CONCAT(REPEAT('  ', niveau), libelle_compte) AS arbre_hierarchique,
        numero_compte,
        niveau,
        parent_id,
        type_entite
    FROM plan_comptable 
    WHERE classe = classe_numero 
    ORDER BY numero_compte;
END //
DELIMITER ;

